package com.kurgan.kursach.models;import lombok.*;import javax.persistence.*;@Entity@Table(name = "card")@Setter@Getter@ToString@AllArgsConstructor@NoArgsConstructorpublic class Card {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private Long card_id;    private String card_code;    private int discount;    @OneToOne    @JoinTable(name = "user_card", joinColumns = @JoinColumn(name = "card_id", referencedColumnName = "card_id"),    inverseJoinColumns = @JoinColumn(name = "user_id", referencedColumnName = "user_id"))    private User user;}