package com.kurgan.kursach.service;import com.kurgan.kursach.models.Car;import com.kurgan.kursach.repository.CarDao;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.data.domain.Sort;import org.springframework.stereotype.Service;import java.util.List;@Servicepublic class CarServiceImpl implements CarService {    @Autowired    private CarDao carDao;    @Override    public void save(Car car) {        carDao.save(car);    }    @Override    public List<Car> findAll() {        return carDao.findAll();    }    @Override    public Car findById(Long id) {        return carDao.getById(id);    }    @Override    public void deleteById(Long id){        carDao.deleteById(id);    }    @Override    public List<Car> sortByFieldAsc(String field){        return carDao.findAll(Sort.by(Sort.Direction.ASC, field));    }}