package com.kurgan.kursach.service;import com.kurgan.kursach.models.Car;import com.kurgan.kursach.models.Card;import com.kurgan.kursach.repository.CardDao;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;@Servicepublic class CardServiceImpl implements CardService {    @Autowired    private CardDao cardDao;    @Override    public void save(Card card) {        cardDao.save(card);    }    @Override    public List<Card> findAll() {        return cardDao.findAll();    }    @Override    public Card findById(Long id) {        return cardDao.getById(id);    }    @Override    public void deleteById(Long id) {        cardDao.deleteById(id);    }    @Override    public List<Card> findAllByDiscount(int discount){        return cardDao.findAllByDiscount(discount);    }}