package com.kurgan.kursach.service;import com.kurgan.kursach.models.Role;import com.kurgan.kursach.models.User;import com.kurgan.kursach.repository.RoleDao;import com.kurgan.kursach.repository.UserDao;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.security.crypto.bcrypt.BCryptPasswordEncoder;import org.springframework.stereotype.Service;import java.util.HashSet;import java.util.List;import java.util.Set;@Servicepublic class UserServiceImpl implements UserService {    @Autowired    private UserDao userDao;    @Autowired    private RoleDao roleDao;    @Autowired    private BCryptPasswordEncoder bCryptPasswordEncoder;    @Override    public void save(User user) {        user.setPassword(bCryptPasswordEncoder.encode(user.getPassword()));        Set<Role> roles = new HashSet<>();        roles.add(roleDao.getById(1L));        user.setRoles(roles);        userDao.save(user);    }    @Override    public User findByUsername(String username) {        return userDao.findByUsername(username);    }    @Override    public void update(User user) {        userDao.save(user);    }    @Override    public List<User> findAll() {        return userDao.findAll();    }    @Override    public void deleteById(Long id) {        userDao.deleteById(id);    }    @Override    public User getById(Long id) {        return userDao.getById(id);    }}